mapper.select:
select fa.ID,fa.ORIG_FILE_NAME,fa.FILE_NAME,fa.FILE_SIZE,FILE_TYPE,fa.FILE_PATH,fa.CREATED_DATE,
fa.CREATED_BY,fa.LAST_MODIFIED_DATE,fa.LAST_MODIFIED_BY
FROM B_MD_GIFT_FILE_ATTACHEMENT fa left join B_MD_GIFT_FILE_MAP fm on fm.file_id = fa.id


        AND fm.APPLICATION_ID  = #{applicationId}


        AND fm.MODULE  = #{module}


        AND fm.[TYPE]  = #{type}

mapper.update:
update B_MD_GIFT_FILE_MAP set APPLICATION_ID = #{applicationId},
    CREATED_DATE = #{createdDate, jdbcType=TIMESTAMP},
    LAST_MODIFIED_DATE = #{lastModifiedDate, jdbcType=TIMESTAMP},
    CREATED_BY = #{createdBy},
    LAST_MODIFIED_BY = #{lastModifiedBy}
WHERE FILE_ID=#{fileId}
